using System.Web.Mvc; using System.Web.Routing;  namespace CTD.Sicil { public class RouteConfig { public static void RegisterRoutes(RouteCollection routes) { routes.IgnoreRoute("{resource}.axd/{*pathInfo}");  routes.MapRoute( name: "Sicil", url: "{controller}/{action}/{sicilno}", defaults: new { controller = "Sicil", action = "Detail", sicilno = UrlParameter.Optional } ); routes.MapRoute( name: "Default", url: "{controller}/{action}/{id}", defaults: new { controller = "Home", action = "Index", id = UrlParameter.Optional } ); } } } 